<template>
  <span>

    <strong class="ion-margin text-muted">{{label}}</strong> <ion-button size="small" color="danger" v-if="CAN_REMOVE" fill="clear" @click="removePhoto()"><ion-icon size="small" :icon="trashOutline"  ></ion-icon></ion-button>

  <ion-thumbnail  @click="uploadPhoto">
    <ion-icon :icon="addCircleOutline" color="white"
              style="position: absolute; margin: 20px; font-size: 50px; color: #ffffff !important;"></ion-icon>
    <ion-spinner v-if="loading" style="position: absolute; margin: 80px;" color="success"></ion-spinner>

    <img :src="preview"
         height="500"
         alt="Wedding photo"/>
  </ion-thumbnail>

  <input
      :id="'selector'+uuid"
      accept="image/*"
      style="display: none"
      type="file"
      @change="selectedPhoto($event)"
  >
  </span>

</template>

<script>
import {IonIcon, IonSpinner, IonThumbnail,IonButton} from "@ionic/vue";
import {addCircleOutline,trashOutline} from "ionicons/icons";
import {FilePicker} from '@capawesome/capacitor-file-picker';
import axios from "axios";

export default {
  props: ["uuid","label"],
  name: "thumbnailComponent",
  components: {IonThumbnail, IonIcon, IonSpinner,IonButton},
  data() {
    return {
      loading: false,
      addCircleOutline,
      trashOutline,
      preview: "data:image/png;base64,",
      Defaultpreview: "data:image/png;base64,"
    }
  },
  methods: {

    removePhoto(){
      this.preview = this.Defaultpreview;

      this.$emit("photoSelected", null);

    },
    selectedPhoto(e) {


      if (!e.target.files){
        return;
      }

      this.loading = true;
      const files = e.target.files[0];

      const formData = new FormData();
      formData.append("file", files);
      axios.post("/save-to-bucket", formData)
          .then(res => {
            const URL = res.data.data.url;
            this.preview = URL;
            this.$emit("photoSelected", {file: files, preview: URL});


            this.loading = false;

          })
          .catch(()=>{
            this.loading=false;
          })
    },


    uploadPhoto() {
      document.getElementById("selector" + this.uuid).click();


    },


    async selectPhoto() {

      // const selector = "selector"+this.uuid;
      //
      // document.getElementById(selector).click();

      const result = await FilePicker.pickFiles({
        types: ['image/*'],
        multiple: false,
        readData: true
      });


      const file = result.files[0];


      this.preview = "data:image/png;base64," + file.data;

      // const rawFile = new File(file.blob, file.name, {
      //   type: file.mimeType,
      // });

      this.$emit("photoSelected", {file: file, preview: this.preview});


    }

  },
  computed: {
    CAN_REMOVE() {

      return this.preview !== this.Defaultpreview;

    }
  }
}
</script>

<style scoped>
ion-thumbnail {
  --size: 100%;
  --border-radius: 14px;
}
</style>