            inputmode="tel"
            placeholder="Mobile Money number"
        ></ion-input>
      </ion-item>

      <ion-item class="ion-margin-top nuna-select-item ion-margin-end ion-margin-start" lines="none">

        <ion-select v-model="network" aria-label="network" interface="action-sheet" mode="ios"
                    placeholder="Select Network">
          <ion-select-option value="MTN">MTN</ion-select-option>
          <ion-select-option value="VDF">Vodafone</ion-select-option>
          <ion-select-option value="ATL">Airtel</ion-select-option>
          <ion-select-option value="TGO">Tigo</ion-select-option>
        </ion-select>

      </ion-item>

      <ion-button
          :disabled="progress"
          class="ion-margin-start ion-margin-end ion-margin-top"
          expand="block"
          mode="ios"
          size="large"
          style="transition: 0.3s ease-in-out"
          @click="withdrawMomo"
      >
        <template v-if="!progress">Send</template>
        <ion-spinner v-if="progress" style="transition: 0.3s ease-in-out"></ion-spinner>
      </ion-button>

      <ion-button
          :disabled="progress"
          class="ion-margin-start ion-margin-end ion-margin-top"
          expand="block"
          fill="outline"
          mode="ios"
          size="large"
          @click="step=3"

      >
        <ion-icon :icon="arrowBackOutline"></ion-icon>
        <template v-if="!progress">Change</template>
        <ion-spinner v-if="progress" style="transition: 0.3s ease-in-out"></ion-spinner>
      </ion-button>


    </div>

    <div v-if="step==5" class="smooth-in">

      <ion-card v-if="amountDue" class="ion-text-center"
                style="background-color: rgba(12,122,224,0.09); border: 1px solid rgba(12,122,224,0.86)">
        <ion-card-content style="color: #0c7ae0">
          A verification code was sent to <strong>{{ user.phone_number }}</strong>, enter the verification code together
          with your mobile money number and network to withdraw your gift.
        </ion-card-content>
      </ion-card>

      <span v-if="!accountConfirmation" style="transition: 0.3s ease-in-out">

      <ion-item class="ion-margin-top" lines="none">
        <ion-input
            v-model="otp"
            class="custom"
            inputmode="number"
            placeholder="Verification Code"
            type="number"
        ></ion-input>
      </ion-item>


      <ion-item class="ion-margin-top " lines="none">
        <ion-input
            v-model="account_number"
            class="custom"
            inputmode="tel"
            placeholder="Account number"
        ></ion-input>
      </ion-item>

      <ion-item class="ion-margin-top nuna-select-item ion-margin-end ion-margin-start" lines="none">

        <ion-select v-model="bank_code"
                    aria-label="network"
                    interface="action-sheet"
                    placeholder="Select Bank"
        >
          <ion-select-option
              v-for="bank in a2zBannks"
              :key="bank.code"
              :value="bank.code">
            {{ bank.name }}
          </ion-select-option>

        </ion-select>

      </ion-item>

      <ion-button
          :disabled="progress"
          class="ion-margin-start ion-margin-end ion-margin-top"
          expand="block"
          mode="ios"
          size="large"
          style="transition: 0.3s ease-in-out"
          @click="withdrawBank"
      >
        <template v-if="!progress">Send</template>
        <ion-spinner v-if="progress" style="transition: 0.3s ease-in-out"></ion-spinner>
      </ion-button>

      <ion-button
          :disabled="progress"
          class="ion-margin-start ion-margin-end ion-margin-top"
          expand="block"
          fill="outline"
          mode="ios"
          size="large"
          @click="step=3"

      >
        <ion-icon :icon="arrowBackOutline"></ion-icon>
        <template v-if="!progress">Change</template>
        <ion-spinner v-if="progress" style="transition: 0.3s ease-in-out"></ion-spinner>
      </ion-button>
      </span>
      <span v-else style="transition: 0.3s ease-in-out">
        <p class="ion-text-center" style="font-weight: lighter; font-size: 28px">Bank Account Confirmed!</p>

        <ion-item class="ion-text-center">
          <ion-label>
            <h1>{{ accountConfirmation.account_name }}</h1>
            <p>Account Name</p>
          </ion-label>
        </ion-item>



      <ion-button
          :disabled="progress"
          class="ion-margin-start ion-margin-end ion-margin-top"
          expand="block"
          mode="ios"
          size="large"
          style="transition: 0.3s ease-in-out"
          @click="confirmBankWithdrawal"
      >
        <template v-if="!progress">Confirm Withdrawal</template>
        <ion-spinner v-if="progress" style="transition: 0.3s ease-in-out"></ion-spinner>
      </ion-button>


          <ion-button
              :disabled="progress"
              class="ion-margin-start ion-margin-end ion-margin-top"
              expand="block"
              fill="outline"
              mode="ios"
              size="large"
              @click="accountConfirmation=null"

          >
      Cancel
      </ion-button>


      </span>


    </div>


  </ion-content>
</template>

<script>
import {
  IonContent,
  IonCard,
  IonCardContent,
  IonButton,
  IonRow,
  IonCol,
  IonIcon,
  IonSpinner,
  IonItem,
  IonInput,
  IonSelect,
  IonSelectOption, toastController, IonLabel
} from "@ionic/vue";
import {chevronForward, arrowBackOutline} from "ionicons/icons";
import VerifyComponent from "@/components/verifyComponent";
import axios from "axios";

export default {
  props: ['weddingID', 'currency'],
  name: "withDrawComponent",
  components: {
    IonSelect,
    IonSelectOption,
    VerifyComponent,
    IonContent,
    IonCard,
    IonCardContent,
    IonButton,
    IonRow,
    IonCol,
    IonIcon,
    IonSpinner,
    IonItem,
    IonInput,
    IonLabel
  },
  data() {
    return {
      step: 1,
      amountDue: null,
      loading: false,
      chevronForward,
      arrowBackOutline,
      phone_number: "",
      otp: "",
      network: null,
      progress: false,
      account_number: "",
      bank_code: "",
      accountConfirmation: null,
      banks: [
        {
          "code": "SCH",
          "name": "STANDARD CHARTERED BANK"
        },
        {
          "code": "ABG",
          "name": "ABSA BANK GHANA LIMITED"
        },
        {
          "code": "GCB",
          "name": "GCB BANK LIMITED"
        },
        {
          "code": "NIB",
          "name": "NATIONAL INVESTMENT BANK"
        },
        {
          "code": "ADB",
          "name": "AGRICULTURAL DEVELOPMENT BANK"
        },
        {
          "code": "UMB",
          "name": "UNIVERSAL MERCHANT BANK"
        },
        {
          "code": "RBL",
          "name": "REPUBLIC BANK LIMITED"
        },
        {
          "code": "ZEN",
          "name": "ZENITH BANK GHANA LTD"
        },
        {
          "code": "ECO",
          "name": "ECOBANK GHANA LTD"
        },
        {
          "code": "CAL",
          "name": "CAL BANK LIMITED"
        },
        {
          "code": "PRD",
          "name": "PRUDENTIAL BANK LTD"
        },
        {
          "code": "STB",
          "name": "STANBIC BANK"
        },
        {
          "code": "GTB",
          "name": "GUARANTY TRUST BANK"
        },
        {
          "code": "UBA",
          "name": "UNITED BANK OF AFRICA"
        },
        {
          "code": "ACB",
          "name": "ACCESS BANK LTD"
        },
        {
          "code": "CBG",
          "name": "CONSOLIDATED BANK GHANA"
        },
        {
          "code": "SGG",
          "name": "SOCIETE GENERALE GHANA"
        },
        {
          "code": "FNB",
          "name": "FIRST NATIONAL BANK"
        },
        {
          "code": "UNL",
          "name": "UNITY LINK"
        },
        {
          "code": "FDL",
          "name": "FIDELITY BANK LIMITED"
        },
        {
          "code": "SIS",
          "name": "SERVICES INTEGRITY SAVINGS & LOANS"
        },
        {
          "code": "BOA",
          "name": "BANK OF AFRICA"
        },
        {
          "code": "DFL",
          "name": "DALEX FINANCE AND LEASING COMPANY"
        },
        {
          "code": "FBO",
          "name": "FIRST BANK OF NIGERIA"
        },
        {
          "code": "GHL",
          "name": "GHL Bank"
        },
        {
          "code": "BOG",
          "name": "BANK OF GHANA"
        },
        {
          "code": "FAB",
          "name": "FIRST ATLANTIC BANK"
        },
        {
          "code": "SSB",
          "name": "SAHEL - SAHARA BANK (BSIC)"
        },
        {
          "code": "GMY",
          "name": "G-MONEY"
        },
        {
          "code": "APX",
          "name": "ARB APEX BANK LIMITED"
        }
      ]

    }
  },
  computed: {
    user() {
      return this.$store.state.user;
    },
    a2zBannks() {
      // eslint-disable-next-line vue/no-side-effects-in-computed-properties
      return this.banks.sort((a, b) => a.name.localeCompare(b.name));
    }
  },
  methods: {
    async showSuccess(message) {


      const toast = await toastController.create({
        message: message,
        duration: 2500,
        position: 'top',
        color: "success",
        mode: "ios"
      });

      await toast.present();

    },

    confirmBankWithdrawal() {
      this.progress = true;

      const data = {
        code: this.otp,
        reference: this.accountConfirmation.reference_id
      };

      axios.post("confirm-bank-withdrawal/"+this.weddingID, data)
          .then(res => {
            this.showSuccess("Withdrawal successful");
            this.step = 3;
            this.getAmountDue();

          })
          .catch(error => {
            this.progress = false;
          })

    },
    withdrawMomo() {

      if (!this.phone_number) {

        this.$store.state.ErrorPosition = "top";
        this.$store.state.errorsArr = {name: ["Mobile money number is required"]};
        this.$store.state.showErrorToast = true;
        return;

      }

      if (!this.otp) {

        this.$store.state.ErrorPosition = "top";
        this.$store.state.errorsArr = {name: ["Verification code is required"]};
        this.$store.state.showErrorToast = true;
        return;

      }

      if (!this.network) {

        this.$store.state.ErrorPosition = "top";
        this.$store.state.errorsArr = {name: ["Select your network"]};
        this.$store.state.showErrorToast = true;
        return;

      }

      this.progress = true;
      axios.post("withdraw-mobile/" + this.weddingID, {
        code: this.otp,
        phone_number: this.phone_number,
        network: this.network
      })
          .then(res => {
            this.progress = false;
            this.showSuccess("Withdrawal successful");

            this.step = 3;
            this.getAmountDue();

          })
          .catch(error => {
            this.progress = false;

          })


    },
    withdrawBank() {

      if (!this.account_number) {

        this.$store.state.ErrorPosition = "top";
        this.$store.state.errorsArr = {name: ["Bank account number is required"]};
        this.$store.state.showErrorToast = true;
        return;

      }

      if (!this.otp) {

        this.$store.state.ErrorPosition = "top";
        this.$store.state.errorsArr = {name: ["Verification code is required"]};
        this.$store.state.showErrorToast = true;
        return;

      }

      if (!this.bank_code) {

        this.$store.state.ErrorPosition = "top";
        this.$store.state.errorsArr = {name: ["Please select your bank"]};
        this.$store.state.showErrorToast = true;
        return;

      }

      this.progress = true;
      axios.post("withdraw-bank/" + this.weddingID, {
        code: this.otp,
        account_number: this.account_number,
        bank_code: this.bank_code
      })
          .then(res => {
            this.progress = false;
            this.accountConfirmation = res.data.data;

          })
          .catch(error => {
            this.progress = false;

          })


    },

    requestOTP(toPage) {

      this.loading = true;
      axios.get("request-otp")
          .then(res => {
            this.step = toPage;
            this.loading = false;
            this.$emit("close");
          })
          .catch(error => {
            this.loading = false;
          })

    },

    getAmountDue() {
      this.loading = true;
      const url = "get-amount-due/" + this.weddingID;
      axios.get(url)
          .then(res => {
            this.amountDue = res.data.data;
            this.loading = false;
          })
          .catch(error => {
            this.loading = false;
          })

    }
  },
  mounted() {

    this.phone_number = this.user.phone_number;

    if (!this.user.approved) {

      this.step = 1;

    } else {

      this.getAmountDue();
      this.step = 3;

    }

  }

}
</script>

